import styled from "styled-components"

export const ContainerApp = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
`
export const Container = styled.div`

    width: 80%;
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    //justify-items: center;
    row-gap: 2rem;
    column-gap: 9rem;
    padding: 2vw;

`
